var fs = require('fs');

var openFile = function (filePath) {
fs.readFile(__dirname + filePath,
      function (err, data) {
        if (err) {
          res.writeHead(500);
          return res.end('Error loading ' + filePath);
        }
		
        res.writeHead(200);
        res.end(data);
      });
}

exports.openFile = openFile;